import { Component, OnInit } from '@angular/core';
import { DataService, DataItem } from '../services/data.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-data',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './data.component.html',
  // Removed styleUrls since the SCSS file does not exist
})
export class DataComponent implements OnInit {
  dataItems: DataItem[] = [];
  newItem: DataItem = { name: '', value: '' };
  loading = false;
  error = '';

  constructor(private dataService: DataService) {}

  ngOnInit() {
    this.loadData();
  }

  loadData() {
    this.loading = true;
    this.dataService.getData().subscribe({
      next: (data) => {
        this.dataItems = data;
        this.loading = false;
      },
      error: (err) => {
        this.error = 'Failed to load data';
        this.loading = false;
      }
    });
  }

  addData() {
    if (!this.newItem.name || !this.newItem.value) {
      this.error = 'Name and Value are required';
      return;
    }
    this.dataService.addData(this.newItem).subscribe({
      next: (item) => {
        this.dataItems.push(item);
        this.newItem = { name: '', value: '' };
        this.error = '';
      },
      error: (err) => {
        this.error = 'Failed to add data';
      }
    });
  }
}
<div class="p-4 max-w-md mx-auto">
  <h2 class="text-2xl font-bold mb-4">Data Items</h2>

  <div *ngIf="loading" class="text-center text-gray-500">Loading...</div>
  <div *ngIf="error" class="text-red-500 mb-4">{{ error }}</div>

  <ul class="mb-6">
    <li *ngFor="let item of dataItems" class="mb-2 border-b border-gray-300 pb-2">
      <strong>{{ item.name }}</strong>: {{ item.value }}
    </li>
  </ul>

  <h3 class="text-xl font-semibold mb-2">Add New Item</h3>
  <form (ngSubmit)="addData()" class="space-y-4">
    <div>
      <label for="name" class="block mb-1 font-medium">Name</label>
      <input id="name" [(ngModel)]="newItem.name" name="name" required class="w-full border border-gray-300 rounded px-3 py-2" />
    </div>
    <div>
      <label for="value" class="block mb-1 font-medium">Value</label>
      <input id="value" [(ngModel)]="newItem.value" name="value" required class="w-full border border-gray-300 rounded px-3 py-2" />
    </div>
    <button type="submit" class="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition">Add</button>
  </form>
</div>
